package hello.core.common;import org.springframework.context.annotation.Scope;import org.springframework.context.annotation.ScopedProxyMode;import org.springframework.stereotype.Component;import javax.annotation.PostConstruct;import javax.annotation.PreDestroy;import java.util.UUID;// 로그를 출력하기 위한 클래스// 이 빈은 HTTP 요청 당 하나씩 생성되고 요청이 끝나는 시점에 소멸된다.@Component// 스프링 컨테이너는 CGLIB 라는 바이트코드를 조작하는 라이브러리를 사용해서// MyLogger를 상속받은 가짜 프록시 객체를 생성한다.// 결과를 확인해보면 우리가 등록한 순수한 MyLogger 클래스가 아니다.// 스프링 컨테이너에 myLogger 라는 이름으로 진짜 대신에 가짜 프록시 객체를 등록한다.// 그래서 의존관계 주입도 이 가짜 프록시 객체가 주입된다.@Scope(value = "request", proxyMode = ScopedProxyMode.TARGET_CLASS)public class MyLogger {    private String uuid;    private String requestURL;    // requestURL 은 빈이 생성되는 시점에는 알 수 없으므로, 외부에서 setter로 입력받는다.    public void setRequestURL(String requestURL) {        this.requestURL = requestURL;    }    public void log(String message) {        System.out.println("[" + uuid + "]" + "[" + requestURL + "] " + message);    }    // 빈이 생성되는 시점에 초기화 메서드를 사용해 uuid를 생성해서 저장해둔다.    // 이 빈은 HTTP 요청 당 하나씩 생성되므로, uuid를 저장해두면 다른 HTTP 요청과 구분할 수 있다.    @PostConstruct    public void init() {        uuid = UUID.randomUUID().toString();        System.out.println("[" + uuid + "] request scope bean create:" + this);    }    // 빈이 소멸되는 시점에 종료 메시지를 남긴다.    @PreDestroy    public void close() {        System.out.println("[" + uuid + "] request scope bean close:" + this);    }}